  changeColor = () => {
    const div = document.getElementById('div1');
    let color = div.style.backgroundColor;

    if(color === 'red') {
      div.style.backgroundColor = 'blue';
      color = 'blue';
    } else {
      div.style.backgroundColor = 'red';
      color = 'red';
    }
  }

  render() {
    return (
      <div className="App">
        <div id='div1' style={{backgroundColor: "red", width: "200px", height: "200px"}}></div>
        <button onClick={changeColor}>Click to change color!</button>
      </div>
    );
  }
}

export default App;
